import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedPackage, peopleCount, pickupTime, totalCost } = location.state || {};
  const [orderId, setOrderId] = useState('');
  const [paymentStatus, setPaymentStatus] = useState('');

  // Early return if no selected package
  if (!selectedPackage) {
    navigate('/');
    return null;
  }

  // Call the backend to create an order
  const createOrder = async () => {
    try {
      const response = await fetch('http://localhost:5000/create-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          amount: totalCost, // Amount in INR
          packageName: selectedPackage.name,
        }),
      });

      const data = await response.json();
      setOrderId(data.orderId); // Set the order ID
    } catch (error) {
      console.error('Error creating order:', error);
    }
  };

  // Simulate the payment process
  const handlePayment = async () => {
    if (!orderId) return;

    // Simulate payment processing by randomly generating success or failure
    const isPaymentSuccessful = Math.random() > 0.5; // 50% chance of success

    const paymentStatus = isPaymentSuccessful ? 'success' : 'failed';
    setPaymentStatus(paymentStatus);

    // Call backend to simulate payment verification
    const response = await fetch('http://localhost:5000/verify-payment', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        orderId,
        paymentStatus,
      }),
    });

    const data = await response.json();
    console.log(data.message);

    if (data.status === 'success') {
      alert('Payment was successful!');
    } else {
      alert('Payment failed. Please try again!');
    }
  };

  // Call the `createOrder` function on initial load or when the dependencies change
  useEffect(() => {
    if (totalCost && selectedPackage) {
      createOrder();
    }
  }, [totalCost, selectedPackage]); // Only re-run when these change

  return (
    <div className="payment-page">
      <div className="payment-container">
        <h1>Payment Page</h1>
        <div className="payment-details">
          <h3>Package Details:</h3>
          <p><strong>Package:</strong> {selectedPackage.name}</p>
          <p><strong>Pickup Time:</strong> {pickupTime}</p>
          <p><strong>Number of People:</strong> {peopleCount}</p>
          <p><strong>Total Cost:</strong> â‚¹{totalCost}</p>
        </div>

        <div className="payment-actions">
          {paymentStatus === '' ? (
            <button onClick={handlePayment}>Make Payment</button>
          ) : (
            <div>
              <p>Payment Status: {paymentStatus === 'success' ? 'Success' : 'Failed'}</p>
              <button onClick={() => setPaymentStatus('')}>Retry</button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
