import React, { useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import axios from 'axios';
import L from 'leaflet';
import { Link } from 'react-router-dom';
import 'leaflet/dist/leaflet.css';
import './Home.css';

// Define custom marker icons to reduce repetitive code
const redMarker = new L.Icon({
  iconUrl: 'https://leafletjs.com/examples/custom-icons/leaf-red.png',
  iconSize: [25, 41],
});

const greenMarker = new L.Icon({
  iconUrl: 'https://leafletjs.com/examples/custom-icons/leaf-green.png',
  iconSize: [25, 41],
});

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving-car');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);

  const fromRef = useRef(null);
  const toRef = useRef(null);
  const mapRef = useRef();

  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  const RAPIDAPI_KEY_GEOCODING = process.env.REACT_APP_RAPIDAPI_KEY_GEOCODING;
  const RAPIDAPI_KEY_ROUTING = process.env.REACT_APP_RAPIDAPI_KEY_ROUTING;

  const fetchSuggestions = async (location, setSuggestions) => {
    if (!location) {
      setSuggestions([]);
      return;
    }

    const url = `https://opencagedata-geocoding.p.rapidapi.com/geocode/v1/json`;
    try {
      const response = await axios.get(url, {
        headers: {
          'X-RapidAPI-Key': RAPIDAPI_KEY_GEOCODING,
          'X-RapidAPI-Host': 'opencagedata-geocoding.p.rapidapi.com',
        },
        params: { q: location, limit: 5 },
      });

      const suggestions = response.data.results.map((place) => ({
        displayName: place.formatted,
        position: { lat: place.geometry.lat, lng: place.geometry.lng },
      }));
      setSuggestions(suggestions);
    } catch (error) {
      console.error('Error fetching places:', error);
    }
  };

  const handlePlaceChange = debounce((location, setSuggestions) => {
    fetchSuggestions(location, setSuggestions);
  }, 300);

  const selectSuggestion = (suggestion, setter, inputRef, setSuggestions) => {
    setter(suggestion.position);
    inputRef.current.value = suggestion.displayName;
    setSuggestions([]);
  };

  const calculateRoute = async () => {
    if (!from || !to) return;

    const url = `https://api.openrouteservice.org/v2/directions/${travelMode}`;
    try {
      const response = await axios.post(
        url,
        { coordinates: [[from.lng, from.lat], [to.lng, to.lat]] },
        {
          headers: {
            Authorization: RAPIDAPI_KEY_ROUTING,
            'Content-Type': 'application/json',
          },
        }
      );

      if (response.data && response.data.routes.length > 0) {
        const routeData = response.data.routes[0];
        const geometry = routeData.geometry.coordinates.map((coord) => ({
          lat: coord[1],
          lng: coord[0],
        }));
        setRoute(geometry);
        setDistance(`${(routeData.summary.distance / 1000).toFixed(2)} km`);
        setDuration(`${Math.round(routeData.summary.duration / 60)} mins`);

        const map = mapRef.current;
        if (map) {
          map.fitBounds(L.latLngBounds(geometry));
        }
      }
    } catch (error) {
      console.error('Error calculating route:', error);
    }
  };

  const swapLocations = () => {
    setFrom(to);
    setTo(from);
  };

  return (
    <div className="home-container">
      {/* Location Selection Section */}
      <div className="location-select">
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="From"
            ref={fromRef}
            onChange={(e) => handlePlaceChange(e.target.value, setFromSuggestions)}
          />
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setFrom, fromRef, setFromSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        <button onClick={swapLocations}>⇄</button>

        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="To"
            ref={toRef}
            onChange={(e) => handlePlaceChange(e.target.value, setToSuggestions)}
          />
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setTo, toRef, setToSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        <button onClick={calculateRoute}>Get Route</button>
      </div>

      {/* Travel Mode Selection */}
      <div className="travel-mode-select">
        <label htmlFor="travelMode">Travel Mode: </label>
        <select
          id="travelMode"
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
        >
          <option value="driving-car">Driving</option>
          <option value="cycling-regular">Cycling</option>
          <option value="foot-walking">Walking</option>
        </select>
      </div>

      {/* Map Container */}
      <MapContainer center={from || { lat: 37.7749, lng: -122.4194 }} zoom={13} ref={mapRef} style={{ height: '400px', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

        {from && <Marker position={from} icon={redMarker} />}
        {to && <Marker position={to} icon={greenMarker} />}

        {route && <Polyline positions={route} color="blue" />}
      </MapContainer>

      {/* Route Information */}
      <div className="route-info">
        {distance && duration ? (
          <div>
            <p>Distance: {distance}</p>
            <p>Approximate Time: {duration}</p>
          </div>
        ) : (
          <p>Please select start and end locations to calculate the route.</p>
        )}
      </div>

      {/* About Section */}
      <div className="app-details">
        <h2>About Our Travel Buddy App</h2>
        <p>Welcome to our Travel Buddy app! We help you plan your journeys with ease.</p>
        <h3>Our Services</h3>
        <ul>
          <li>Route planning and navigation</li>
          <li>Travel distance and time estimation</li>
          <li>Place suggestions for your travel needs</li>
          <li>Multiple travel modes for your convenience</li>
        </ul>
      </div>

      {/* Popular Sections */}
      <div className="popular-sections">
        {/* Popular Destinations Section */}
        <div className="popular-destinations">
          <h2>Popular Destinations</h2>
          <div className="destination">
            <img src="/path/to/iskcon-temple.jpg" alt="ISKCON Temple" className="destination-image" />
            <div className="destination-info">
              <h3>ISKCON Temple</h3>
              <p>Rajajinagar</p>
              <div className="rating">4.2 ⭐</div>
            </div>
          </div>
          {/* ... other destinations */}
        </div>

        {/* Popular Events Section */}
        <div className="popular-events">
          <h2>Popular Events</h2>
          <div className="event">
            <img src="/path/to/alan-walker.jpg" alt="Alan Walker" className="event-image" />
            <div className="event-info">
              <h3>Alan Walker Concert</h3>
              <p>Forum Mall</p>
              <div className="rating">4.2 ⭐</div>
            </div>
          </div>
          {/* ... other events */}
        </div>
      </div>
    </div>
  );
};

export default Home;
