import React, { useState, useRef, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import axios from 'axios';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Home.css';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving-car'); // Default travel mode
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);

  const fromRef = useRef(null);
  const toRef = useRef(null);
  const mapRef = useRef();

  // Debounce function to limit API calls
  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  const fetchSuggestions = async (location, setter, setSuggestions) => {
    if (location.length === 0) {
      setSuggestions([]);
      return;
    }
    const url = `https://nominatim.openstreetmap.org/search?format=json&q=${location}`;
    try {
      const response = await axios.get(url);
      setSuggestions(response.data.map(place => ({
        displayName: place.display_name,
        position: { lat: parseFloat(place.lat), lng: parseFloat(place.lon) },
      })));
    } catch (error) {
      console.error("Error fetching places:", error);
    }
  };

  const handlePlaceChange = debounce((location, setter, setSuggestions) => {
    fetchSuggestions(location, setter, setSuggestions);
  }, 300);

  const selectSuggestion = (suggestion, setter, inputRef, setSuggestions) => {
    setter(suggestion.position);
    inputRef.current.value = suggestion.displayName;
    setSuggestions([]);
  };

  const calculateRoute = async () => {
    if (from && to) {
      const url = `https://api.openrouteservice.org/v2/directions/${travelMode}`;
      try {
        const response = await axios.post(
          url,
          {
            coordinates: [[from.lng, from.lat], [to.lng, to.lat]],
          },
          {
            headers: {
              'Authorization': 'YOUR_API_KEY', // Replace with your OpenRouteService API key
              'Content-Type': 'application/json',
            },
          }
        );
        if (response.data && response.data.routes.length > 0) {
          const routeData = response.data.routes[0];
          const geometry = routeData.geometry.coordinates.map(coord => ({ lat: coord[1], lng: coord[0] }));
          
          // Set a delay before displaying the route
          setTimeout(() => {
            setRoute(geometry);
            setDistance((routeData.summary.distance / 1000).toFixed(2) + ' km'); // Convert meters to km
            setDuration(Math.round(routeData.summary.duration / 60) + ' mins'); // Convert seconds to mins

            // Center map on the route
            const map = mapRef.current;
            if (map) {
              const bounds = L.latLngBounds(geometry);
              map.fitBounds(bounds);
            }
          }, 1000); // Delay in milliseconds (1000 ms = 1 second)
        }
      } catch (error) {
        console.error("Error calculating route:", error);
      }
    }
  };

  const swapLocations = () => {
    const temp = from;
    setFrom(to);
    setTo(temp);
  };

  return (
    <div className="home-container">
      {/* Location Input Section */}
      <div className="location-select">
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="From"
            ref={fromRef}
            onChange={(e) => handlePlaceChange(e.target.value, setFrom, setFromSuggestions)}
          />
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setFrom, fromRef, setFromSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>
        
        <button onClick={swapLocations}>⇄</button>

        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="To"
            ref={toRef}
            onChange={(e) => handlePlaceChange(e.target.value, setTo, setToSuggestions)}
          />
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setTo, toRef, setToSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>
        
        <button onClick={calculateRoute}>Get Route</button>
      </div>

      {/* Travel Mode Selection */}
      <div className="travel-mode-select">
        <label htmlFor="travelMode">Travel Mode: </label>
        <select
          id="travelMode"
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
        >
          <option value="driving-car">Driving</option>
          <option value="cycling-regular">Cycling</option>
          <option value="foot-walking">Walking</option>
        </select>
      </div>

      {/* Map Display */}
      <MapContainer center={from || { lat: 37.7749, lng: -122.4194 }} zoom={13} ref={mapRef} style={{ height: '400px', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        
        {from && <Marker position={from} icon={new L.Icon({ iconUrl: 'https://leafletjs.com/examples/custom-icons/leaf-red.png', iconSize: [25, 41] })} />}
        {to && <Marker position={to} icon={new L.Icon({ iconUrl: 'https://leafletjs.com/examples/custom-icons/leaf-green.png', iconSize: [25, 41] })} />}
        
        {route && <Polyline positions={route} color="blue" />}
      </MapContainer>

      {/* Distance and Duration Info */}
      <div className="route-info">
        {distance && duration && (
          <div>
            <p>Distance: {distance}</p>
            <p>Approximate Time: {duration}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
