import React, { useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Payment = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  const { amount } = location.state || { amount: 0 };

  useEffect(() => {
    if (amount > 0) {
      // Simulating a delay for payment process
      setTimeout(() => {
        // Redirect to a mock payment gateway URL (this should be replaced with actual gateway integration)
        window.location.href = `https://mockpaymentgateway.com/pay?amount=${amount}`;
      }, 2000);
    }
  }, [amount]);

  return (
    <div className="payment">
      <h1>Payment</h1>
      <p>Amount to be paid: ${amount}</p>
      <p>Redirecting to payment gateway...</p>
    </div>
  );
};

export default Payment;
