import React, { useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import axios from 'axios';
import L from 'leaflet';
import { Link } from 'react-router-dom'; // Ensure Link is imported
import 'leaflet/dist/leaflet.css';
import './Home.css';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving-car'); // Default travel mode
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);

  const fromRef = useRef(null);
  const toRef = useRef(null);
  const mapRef = useRef();

  // Other existing code here...

  return (
    <div className="home-container">
      {/* Existing components for map, route, and app details */}

      {/* Popular Destinations Section */}
      <div className="popular-destinations">
        <h2>Popular Destinations</h2>
        {/* Destination cards */}
        <div className="destination">
          <img src="/path/to/iskcon-temple.jpg" alt="ISKCON Temple" className="destination-image" />
          <div className="destination-info">
            <h3>ISKCON Temple</h3>
            <p>Rajajinagar</p>
            <div className="rating">4.2 ⭐</div>
          </div>
        </div>
        <div className="destination">
          <img src="/path/to/mysore-palace.jpg" alt="Mysore Palace" className="destination-image" />
          <div className="destination-info">
            <h3>Mysore Palace</h3>
            <p>Mysore</p>
            <div className="rating">4.8 ⭐</div>
          </div>
        </div>
        <div className="destination">
          <img src="/path/to/anjaneya-hills.jpg" alt="Anjaneya Hills" className="destination-image" />
          <div className="destination-info">
            <h3>Anjaneya Hills</h3>
            <p>Hampi</p>
            <div className="rating">4.5 ⭐</div>
          </div>
        </div>
        <Link to="/destinations" className="view-more">Go to Popular Destinations</Link>
      </div>

      {/* New Popular Events Section */}
      <div className="popular-events">
        <h2>Popular Events</h2>
        <div className="event">
          <img src="/path/to/alan-walker.jpg" alt="Alan Walker" className="event-image" />
          <div className="event-info">
            <h3>Alan Walker Concert</h3>
            <p>Forum Mall</p>
            <div className="rating">4.2 ⭐</div>
          </div>
        </div>
        <div className="event">
          <img src="/path/to/food-festival.jpg" alt="Food Festival" className="event-image" />
          <div className="event-info">
            <h3>Food Festival</h3>
            <p>Lalbagh</p>
            <div className="rating">4.7 ⭐</div>
          </div>
        </div>
        <div className="event">
          <img src="/path/to/art-expo.jpg" alt="Art Expo" className="event-image" />
          <div className="event-info">
            <h3>Art Expo</h3>
            <p>City Art Gallery</p>
            <div className="rating">4.6 ⭐</div>
          </div>
        </div>
        <Link to="/events" className="view-more">Go to Popular Events</Link>
      </div>
    </div>
  );
};

export default Home;
