import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './BookATrip.css';

const BookATrip = () => {
  const [name, setName] = useState('');
  const [pickupPoint, setPickupPoint] = useState('');
  const [placesOfVisi, setPlacesOfVisit] = useState('');
  const [modeOfTransport, setModeOfTransport] = useState('');
  const [totalPeople, setTotalPeople] = useState('');
  const navigate = useNavigate();

  const handleBookNow = async () => {
    const details = {
      name,
      pickupPoint,
      placesOfVisit,
      modeOfTransport,
      totalPeople,
    };

    try {
      // Send data to backend
      const response = await fetch('http://localhost:5000/api/trips', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(details),
      });

      if (response.ok) {
        const trip = await response.json();
        // Redirect to TripDetails page with the saved trip data
        navigate(`/trip-details/${trip.id}`, { state: trip });
      } else {
        alert('Failed to book trip');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error booking the trip');
    }
  };

  return (
    <div className="book-trip-page">
      <h1>BOOK A TRIP</h1>
      <div className="form-section">
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        
        <label>Pickup Point:</label>
        <input type="text" value={pickupPoint} onChange={(e) => setPickupPoint(e.target.value)} />
        
        <label>Places of Visit:</label>
        <input type="text" value={DropPoint} onChange={(e) => setPlacesOfVisit(e.target.value)} />
        
        <label>Mode of Transport:</label>
        <input type="text" value={modeOfTransport} onChange={(e) => setModeOfTransport(e.target.value)} />
        
        <label>Total No. of People:</label>
        <input type="text" value={totalPeople} onChange={(e) => setTotalPeople(e.target.value)} />

        <button className="book-now" onClick={handleBookNow}>BOOK NOW</button>
      </div>
    </div>
  );
};

export default BookATrip;
