import React, { useState, useRef, useEffect } from 'react';
import { GoogleMap, LoadScript, Marker, DirectionsRenderer } from '@react-google-maps/api';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('DRIVING');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);
  const [placeDetails, setPlaceDetails] = useState(null); // To hold place details
  
  const fromRef = useRef(null);
  const toRef = useRef(null);
  
  const [googleLoaded, setGoogleLoaded] = useState(false);

  const GOOGLE_API_KEY = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;

  // Fetch suggestions from Places API (Autocomplete)
  const fetchSuggestions = async (location, setter, setSuggestions) => {
    if (location.length === 0) {
      setSuggestions([]);
      return;
    }

    const url = `https://maps.googleapis.com/maps/api/place/autocomplete/json`;
    try {
      const response = await axios.get(url, {
        params: {
          input: location,
          key: GOOGLE_API_KEY,
        },
      });
      setSuggestions(response.data.predictions.map((place) => ({
        displayName: place.description,
        place_id: place.place_id, // Save the place_id for the details API
        position: place.geometry ? { lat: place.geometry.location.lat, lng: place.geometry.location.lng } : null,
      })));
    } catch (error) {
      console.error("Error fetching places:", error);
    }
  };

  // Fetch place details from RapidAPI
  const fetchPlaceDetails = async (placeId) => {
    const options = {
      method: 'GET',
      url: 'https://google-map-places.p.rapidapi.com/maps/api/place/details/json',
      params: {
        place_id: placeId,
        region: 'en',
        fields: 'all', // You can adjust fields to get specific details
        language: 'en',
        reviews_no_translations: 'true',
      },
      headers: {
        'x-rapidapi-key': '23eaea1cefmsh2dd07190da91198p15b699jsnc2a14317fe24', // Your RapidAPI Key
        'x-rapidapi-host': 'google-map-places.p.rapidapi.com',
      },
    };

    try {
      const response = await axios.request(options);
      setPlaceDetails(response.data.result); // Save the place details in state
    } catch (error) {
      console.error('Error fetching place details:', error);
    }
  };

  // Handle place change with debounce
  const handlePlaceChange = debounce((location, setter, setSuggestions) => {
    fetchSuggestions(location, setter, setSuggestions);
  }, 300);

  // Select a suggestion and fetch place details
  const selectSuggestion = (suggestion, setter, inputRef, setSuggestions) => {
    setter(suggestion.position); // Set the place position
    inputRef.current.value = suggestion.displayName; // Set the display name in the input
    setSuggestions([]); // Clear the suggestions
    fetchPlaceDetails(suggestion.place_id); // Fetch place details
  };

  // Calculate the route using Directions API
  const calculateRoute = async () => {
    if (from && to) {
      setRoute(null);  // Reset previous route
      setDistance('');
      setDuration('');

      if (googleLoaded) { // Check if Google Maps API is loaded
        const directionsService = new google.maps.DirectionsService();
        const request = {
          origin: from,
          destination: to,
          travelMode: google.maps.TravelMode[travelMode],
        };

        directionsService.route(request, (result, status) => {
          if (status === google.maps.DirectionsStatus.OK) {
            setRoute(result);
            const routeData = result.routes[0].legs[0];
            setDistance(routeData.distance.text);
            setDuration(routeData.duration.text);
          } else {
            console.error("Error calculating route:", status);
          }
        });
      } else {
        console.error('Google Maps API is not loaded.');
      }
    } else {
      alert("Please select both the start and end locations.");
    }
  };

  // Swap the start and end locations
  const swapLocations = () => {
    const temp = from;
    setFrom(to);
    setTo(temp);
    setFromSuggestions([]); // Clear the suggestions when swapping
    setToSuggestions([]);
  };

  // When LoadScript has loaded the Google API, set the googleLoaded state to true
  const onGoogleMapLoad = () => {
    setGoogleLoaded(true);
  };

  return (
    <div className="home-container">
      <div className="location-select">
        {/* From Location Input */}
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="From"
            ref={fromRef}
            onChange={(e) => handlePlaceChange(e.target.value, setFrom, setFromSuggestions)}
          />
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setFrom, fromRef, setFromSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Swap Button */}
        <button onClick={swapLocations}>⇄</button>

        {/* To Location Input */}
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="To"
            ref={toRef}
            onChange={(e) => handlePlaceChange(e.target.value, setTo, setToSuggestions)}
          />
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setTo, toRef, setToSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Get Route Button */}
        <button onClick={calculateRoute}>Get Route</button>
      </div>

      <div className="travel-mode-select">
        <label htmlFor="travelMode">Travel Mode: </label>
        <select
          id="travelMode"
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
        >
          <option value="DRIVING">Driving</option>
          <option value="BICYCLING">Cycling</option>
          <option value="WALKING">Walking</option>
        </select>
      </div>

      {/* Google Map with Directions */}
      <LoadScript googleMapsApiKey={GOOGLE_API_KEY} onLoad={onGoogleMapLoad}>
        <GoogleMap
          center={from || { lat: 37.7749, lng: -122.4194 }} // Default to SF
          zoom={13}
          mapContainerStyle={{ height: '400px', width: '100%' }}
        >
          {from && <Marker position={from} />}
          {to && <Marker position={to} />}
          {route && <DirectionsRenderer directions={route} />}
        </GoogleMap>
      </LoadScript>

      {/* Route Information */}
      <div className="route-info">
        {distance && duration ? (
          <div>
            <p>Distance: {distance}</p>
            <p>Approximate Time: {duration}</p>
          </div>
        ) : (
          <p>Please select start and end locations to calculate the route.</p>
        )}
      </div>

      {/* Place Details */}
      <div className="place-details">
        {placeDetails ? (
          <div>
            <h3>{placeDetails.name}</h3>
            <p>{placeDetails.formatted_address}</p>
            {/* Show more place details like photos and reviews */}
          </div>
        ) : (
          <p>Select a place to view its details.</p>
        )}
      </div>

      {/* Popular Destinations Section */}
      <div className="popular-destinations">
        <h2>Popular Destinations</h2>
        <div className="destination">
          <img src="/path/to/iskcon-temple.jpg" alt="ISKCON Temple" className="destination-image" />
          <div className="destination-info">
            <h3>ISKCON Temple</h3>
            <p>Rajajinagar</p>
            <div className="rating">4.2 ⭐</div>
          </div>
        </div>
        <div className="destination">
          <img src="/path/to/mysore-palace.jpg" alt="Mysore Palace" className="destination-image" />
          <div className="destination-info">
            <h3>Mysore Palace</h3>
            <p>Mysore</p>
            <div className="rating">4.8 ⭐</div>
          </div>
        </div>
        <Link to="/destinations" className="view-more">Go to Popular Destinations</Link>
      </div>

      {/* Popular Events Section */}
      <div className="popular-events">
        <h2>Popular Events</h2>
        <div className="event">
          <img src="/path/to/alan-walker.jpg" alt="Alan Walker" className="event-image" />
          <div className="event-info">
            <h3>Alan Walker Concert</h3>
            <p>Forum Mall</p>
            <div className="rating">4.2 ⭐</div>
          </div>
        </div>
        <div className="event">
          <img src="/path/to/food-festival.jpg" alt="Food Festival" className="event-image" />
          <div className="event-info">
            <h3>Food Festival</h3>
            <p>Lalbagh</p>
            <div className="rating">4.7 ⭐</div>
          </div>
        </div>
        <Link to="/events" className="view-more">Go to Popular Events</Link>
      </div>
    </div>
  );
};

export default Home;
