import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedPackage, peopleCount, pickupTime, totalCost } = location.state || {};
  const [paytmParams, setPaytmParams] = useState(null);

  if (!selectedPackage) {
    navigate('/');
    return null;
  }

  // Call the backend to create a Paytm order and get the payment details
  const createPaytmOrder = async () => {
    try {
      const response = await fetch('http://localhost:5000/create-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          amount: totalCost, // Amount in INR
        }),
      });

      const data = await response.json();
      setPaytmParams(data.paytmParams);
    } catch (error) {
      console.error('Error creating Paytm order:', error);
    }
  };

  useEffect(() => {
    createPaytmOrder();
  }, [totalCost, selectedPackage]);

  const handlePayNow = () => {
    if (paytmParams) {
      const form = document.createElement('form');
      form.method = 'POST';
      form.action = 'https://secure.paytm.in/theia/processTransaction';

      // Append Paytm payment parameters to the form
      for (const key in paytmParams) {
        const input = document.createElement('input');
        input.type = 'hidden';
        input.name = key;
        input.value = paytmParams[key];
        form.appendChild(input);
      }

      document.body.appendChild(form);
      form.submit(); // Submit the form to redirect to Paytm for payment
    }
  };

  return (
    <div className="payment-page">
      <div className="payment-container">
        <h1>Payment Page</h1>
        <div className="payment-details">
          <h3>Package Details:</h3>
          <p><strong>Package:</strong> {selectedPackage.name}</p>
          <p><strong>Pickup Time:</strong> {pickupTime}</p>
          <p><strong>Number of People:</strong> {peopleCount}</p>
          <p><strong>Total Cost:</strong> â‚¹{totalCost}</p>
        </div>

        <div className="payment-actions">
          <button onClick={handlePayNow}>Pay Now</button>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
