import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedPackage, peopleCount, pickupTime, totalCost } = location.state || {};
  const [orderId, setOrderId] = useState('');
  const [isLoading, setIsLoading] = useState(true); // Flag to track loading state

  if (!selectedPackage) {
    // Redirect if no package data is found
    navigate('/');
    return null;
  }

  // Create a mock order
  const createOrder = async () => {
    try {
      const response = await fetch('http://localhost:5000/create-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          amount: totalCost, // Amount in INR
          packageName: selectedPackage.name,
        }),
      });
      const data = await response.json();
      setOrderId(data.orderId); // Set the order ID returned by the backend
    } catch (error) {
      console.error('Error creating order:', error);
    } finally {
      setIsLoading(false); // Set loading state to false once order is created
    }
  };

  // Simulate the payment process
  const handlePayNow = async () => {
    if (!orderId) return;

    // Simulate a random payment status (success or failure)
    const isPaymentSuccessful = Math.random() > 0.5; // 50% chance of success
    const paymentStatus = isPaymentSuccessful ? 'success' : 'failed';

    // Call backend to verify the payment status
    const response = await fetch('http://localhost:5000/verify-payment', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        orderId,
        paymentStatus,
      }),
    });

    const data = await response.json();

    if (data.status === 'success') {
      alert('Payment was successful!');
    } else {
      alert('Payment failed. Please try again!');
    }
  };

  // Call the createOrder function on initial load or when totalCost or selectedPackage changes
  useEffect(() => {
    if (totalCost && selectedPackage) {
      createOrder(); // Create order if totalCost and selectedPackage are available
    }
  }, [totalCost, selectedPackage]); // Re-run effect only when totalCost or selectedPackage changes

  return (
    <div className="payment-page">
      <div className="payment-container">
        <h1>Payment Page</h1>
        <div className="payment-details">
          <h3>Package Details:</h3>
          <p><strong>Package:</strong> {selectedPackage.name}</p>
          <p><strong>Pickup Time:</strong> {pickupTime}</p>
          <p><strong>Number of People:</strong> {peopleCount}</p>
          <p><strong>Total Cost:</strong> ₹{totalCost}</p>
        </div>

        <div className="payment-actions">
          {isLoading ? (
            <p>Loading order...</p>
          ) : (
            <button onClick={handlePayNow}>Pay Now</button>
          )}
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
