import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import logoImage from '../images/logo-modified.png';
import './signup.css';

const Signup = () => {
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const navigate = useNavigate();

  const isValidPhone = (phone) => /^\d{10}$/.test(phone);  // Basic phone validation

  const handleSignup = async () => {
    if (!isValidPhone(phone)) {
      alert('Please enter a valid 10-digit phone number');
      return;
    }

    if (password === confirmPassword) {
      try {
        // Send signup request to the backend
        const response = await axios.post('http://localhost:5000/api/signup', { email, phone, password });
        console.log('Signup successful:', response.data);
        navigate('/login');  // Redirect to login page after successful signup
      } catch (error) {
        console.error('Signup failed:', error.response?.data);
        alert(error.response?.data?.message || 'An error occurred during signup.');
      }
    } else {
      alert('Passwords do not match');
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-header">
        <img src={logoImage} alt="Logo" className="auth-logo" />
        <h1>TRAVELBUDDY</h1>
      </div>
      <h2>Sign Up</h2>
      <input 
        type="email" 
        placeholder="Email" 
        value={email} 
        onChange={(e) => setEmail(e.target.value)} 
      />
      <input 
        type="text" 
        placeholder="Phone Number" 
        value={phone} 
        onChange={(e) => setPhone(e.target.value)} 
      />
      <input 
        type="password" 
        placeholder="Password" 
        value={password} 
        onChange={(e) => setPassword(e.target.value)} 
      />
      <input 
        type="password" 
        placeholder="Confirm Password" 
        value={confirmPassword} 
        onChange={(e) => setConfirmPassword(e.target.value)} 
      />
      <button onClick={handleSignup}>Sign Up</button>
    </div>
  );
};

export default Signup;




