import React from 'react';
import { Link } from 'react-router-dom';
import './PopularDestinations.css';

// Import images directly
import IskconImage from '../images/iskcon-temple.jpg';
import LalbaghImage from '../images/lalbagh.jpg';

const destinations = [
  {
    id: 1,
    name: 'ISKCON Temple',
    location: 'Rajajinagar',
    rating: 4.2,
    image: IskconImage, // Use imported image
    detailPage: '/destinations/iskcon-temple'
  },
  {
    id: 2,
    name: 'Lalbagh Botanical Garden',
    location: 'Bangalore',
    rating: 4.6,
    image: LalbaghImage, // Use imported image
    detailPage: '/destinations/lalbagh'
  },
  // Add more destinations as needed
];

function PopularDestinations() {
  return (
    <div className="component-container">
      <h2>POPULAR DESTINATIONS</h2>
      {destinations.map((destination) => (
       <Link to={`/destinations/${destination.id}`} key={destination.id} className="destination">

          <div
            className="image-placeholder"
            style={{ backgroundImage: `url(${destination.image})` }}
          ></div>
          <div className="destination-info">
            <h3>{destination.name}</h3>
          <p>{destination.location}</p>
            <div className="rating">{destination.rating} ‚≠ê</div>
          </div>
        </Link>
      ))}
    </div>
  );
}

export default PopularDestinations;
