import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './TripDetails.css';

const TripDetails = () => {
  const { id } = useParams();  // Get the trip ID from the URL
  const [trip, setTrip] = useState(null);

  useEffect(() => {
    const fetchTripDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/trips/${id}`);
        if (response.ok) {
          const tripData = await response.json();
          setTrip(tripData);  // Set the trip data to state
        } else {
          console.error('Trip not found');
          alert('Trip not found');
        }
      } catch (error) {
        console.error('Error fetching trip details:', error);
        alert('Error fetching trip details');
      }
    };

    if (id) {
      fetchTripDetails();  // Fetch the trip details when the ID is available
    }
  }, [id]);

  if (!trip) {
    return <div>Loading...</div>;  // Show loading message while fetching trip details
  }

  return (
    <div className="trip-details-page">
      <h1>Trip Details</h1>
      <div className="trip-info">
        <p><strong>Name:</strong> {trip.name}</p>
        <p><strong>Pickup Point:</strong> {trip.pickupPoint}</p>
        <p><strong>Places of Visit:</strong> {trip.placesOfVisit}</p>
        <p><strong>Mode of Transport:</strong> {trip.modeOfTransport}</p>
        <p><strong>Total People:</strong> {trip.totalPeople}</p>
      </div>
    </div>
  );
};

export default TripDetails;
