import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './TravelPackage.css';

import BangaloreCityTourImage from '../images/bangalore-city-tour.jpg';
import NandiHillsImage from '../images/nandi-hills.jpg';
import RamanagaraClimbingImage from '../images/ramanagara-climbing.jpg';
import KabiniSafariImage from '../images/kabini-safari.jpg';

const TravelPackage = () => {
  const navigate = useNavigate();
  const [peopleCount, setPeopleCount] = useState(1);
  const [pickupTime, setPickupTime] = useState('8am');
  const [selectedPackageId, setSelectedPackageId] = useState(1);

  // Define package details (prices, per person cost, etc.)
  const packages = [
    {
      id: 1,
      name: 'Bangalore City Tour',
      description: 'Explore the best of Bangalore with a guided city tour covering major landmarks.',
      price: 3000, // base price
      perPersonCost: 1000, // cost per person
      image: BangaloreCityTourImage,
    },
    {
      id: 2,
      name: 'Nandi Hills Adventure',
      description: 'Trek up to Nandi Hills for a panoramic view and enjoy a picnic at the top.',
      price: 2000,
      perPersonCost: 500,
      image: NandiHillsImage,
    },
    {
      id: 3,
      name: 'Ramanagara Rock Climbing',
      description: 'Adventure awaits you in Ramanagara with rock climbing and hiking activities.',
      price: 4000,
      perPersonCost: 1200,
      image: RamanagaraClimbingImage,
    },
    {
      id: 4,
      name: 'Kabini Wildlife Safari',
      description: 'Embark on an exciting safari through Kabini forest and spot wildlife in their natural habitat.',
      price: 5000,
      perPersonCost: 1500,
      image: KabiniSafariImage,
    },
  ];

  // Get selected package details based on the selectedPackageId
  const selectedPackage = packages.find((pkg) => pkg.id === selectedPackageId);

  // Calculate the total cost based on the selected package and number of people
  const totalCost = selectedPackage
    ? selectedPackage.price + selectedPackage.perPersonCost * (peopleCount - 1)
    : 0;

  // Handle booking action
  const handleBookNow = () => {
    navigate('/bookatrip');
  };

  return (
    <div className="travel-package-container">
      <h1>Travel Packages - Bangalore</h1>

      {/* Dropdown for selecting package */}
      <div className="dropdown-container">
        <label htmlFor="package-selection">Select Package:</label>
        <select
          id="package-selection"
          value={selectedPackageId}
          onChange={(e) => setSelectedPackageId(Number(e.target.value))}
        >
          {packages.map((pkg) => (
            <option key={pkg.id} value={pkg.id}>
              {pkg.name}
            </option>
          ))}
        </select>
      </div>

      {/* Package details display */}
      <div className="package-info">
        <img src={selectedPackage.image} alt={selectedPackage.name} className="package-image" />
        <h3>{selectedPackage.name}</h3>
        <p>{selectedPackage.description}</p>
        <p><strong>Price per day:</strong> ₹{selectedPackage.price}</p>
      </div>

      {/* Dropdown for selecting pickup time */}
      <div className="dropdown-container">
        <label htmlFor="pickup-time">Time of Pickup:</label>
        <select
          id="pickup-time"
          value={pickupTime}
          onChange={(e) => setPickupTime(e.target.value)}
        >
          <option value="8am">8:00 AM</option>
          <option value="10am">10:00 AM</option>
          <option value="12pm">12:00 PM</option>
          <option value="2pm">2:00 PM</option>
          <option value="4pm">4:00 PM</option>
        </select>
      </div>

      {/* Number of people input */}
      <div className="dropdown-container">
        <label htmlFor="people-count">Number of People:</label>
        <input
          type="range"
          id="people-count"
          min="1"
          max="20"
          value={peopleCount}
          onChange={(e) => setPeopleCount(e.target.value)}
          step="1"
        />
        <span>{peopleCount} person(s)</span>
      </div>

      {/* Total cost display */}
      <div className="cost-display">
        <p><strong>Total Cost for {peopleCount} person(s):</strong> ₹{totalCost}</p>
      </div>

      {/* Book Now Button */}
      <div className="book-now-container">
        <button onClick={handleBookNow}>Book Now</button>
      </div>
    </div>
  );
};

export default TravelPackage;
