import { Link } from 'react-router-dom';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // For custom marker icons
import './Home.css';

import iskconTempleImg from '../images/iskcon-temple.jpg';
import lalbaghImg from '../images/lalbagh.jpg';
import cubbonParkImg from '../images/cubbon-park.jpg';
import concertImg from '../images/concert.jpg';
import marathonImg from '../images/marathon.jpg';
import foodFestivalImg from '../images/food-festival.jpg';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);
  const fromRef = useRef(null);
  const toRef = useRef(null);

  const OPENROUTESERVICE_API_KEY = process.env.REACT_APP_OPENROUTESERVICE_API_KEY;

  // Debounce function for handling input change delay
  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  // Fetch place suggestions from OpenRouteService API
  const fetchSuggestions = async (location, setter, setSuggestions) => {
    if (location.length === 0) {
      setSuggestions([]);
      return;
    }

    try {
      const response = await axios.get('https://api.openrouteservice.org/geocode/search', {
        params: {
          api_key: OPENROUTESERVICE_API_KEY,
          text: location,
        },
      });

      if (response.data.features && response.data.features.length > 0) {
        setSuggestions(response.data.features.map((place) => ({
          displayName: place.properties.label,
          position: { lat: place.geometry.coordinates[1], lng: place.geometry.coordinates[0] },
        })));
      } else {
        setSuggestions([]);
      }
    } catch (error) {
      console.error('Error fetching places:', error);
      setSuggestions([]);
    }
  };

  const handlePlaceChange = debounce((location, setter, setSuggestions) => {
    fetchSuggestions(location, setter, setSuggestions);
  }, 300);

  const selectSuggestion = (suggestion, setter, inputRef, setSuggestions) => {
    setter(suggestion.position);
    inputRef.current.value = suggestion.displayName;
    setSuggestions([]);
  };

  const calculateRoute = async () => {
    if (from && to) {
      setRoute(null);
      setDistance('');
      setDuration('');

      try {
        const response = await axios.post('https://api.openrouteservice.org/v2/directions/driving-car', {
          coordinates: [
            [from.lng, from.lat],
            [to.lng, to.lat],
          ],
        }, {
          headers: {
            Authorization: `Bearer ${OPENROUTESERVICE_API_KEY}`,
          },
        });

        const routeData = response.data.routes[0];
        const dist = routeData.summary.distance / 1000;
        const duration = routeData.summary.duration / 60;

        setDistance(`${dist.toFixed(2)} km`);
        setDuration(`${Math.round(duration)} mins`);

        // Set the route to display on the map
        setRoute(routeData.geometry.coordinates);
      } catch (error) {
        console.error('Error fetching route:', error);
        alert('Failed to calculate route. Please try again.');
      }
    } else {
      alert("Please select both the start and end locations.");
    }
  };

  const swapLocations = () => {
    const temp = from;
    setFrom(to);
    setTo(temp);
    setFromSuggestions([]);
    setToSuggestions([]);
  };

  const popularDestinations = [
    { name: "ISKCON Temple", location: "Rajajinagar, Bangalore", rating: "4.2 ⭐", image: iskconTempleImg },
    { name: "Lalbagh Botanical Garden", location: "Mavalli, Bangalore", rating: "4.7 ⭐", image: lalbaghImg },
    { name: "Cubbon Park", location: "Bangalore", rating: "4.5 ⭐", image: cubbonParkImg },
  ];

  const popularEvents = [
    { name: "Live Concert by XYZ Band", location: "ABC Arena, Bangalore", date: "12th December 2024", image: concertImg },
    { name: "City Marathon 2024", location: "Downtown Park, Bangalore", date: "5th January 2024", image: marathonImg },
    { name: "Annual Food Festival", location: "City Square, Bangalore", date: "20th November 2024", image: foodFestivalImg },
  ];

  return (
    <div className="home-container">
      {/* Location Selection Section */}
      <div className="location-select">
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="From"
            ref={fromRef}
            onChange={(e) => handlePlaceChange(e.target.value, setFrom, setFromSuggestions)}
          />
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setFrom, fromRef, setFromSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        <button onClick={swapLocations}>⇄</button>

        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="To"
            ref={toRef}
            onChange={(e) => handlePlaceChange(e.target.value, setTo, setToSuggestions)}
          />
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setTo, toRef, setToSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        <div className="travel-mode-selector">
          <label>
            <input
              type="radio"
              value="driving"
              checked={travelMode === 'driving'}
              onChange={() => setTravelMode('driving')}
            />
            Driving
          </label>
          <label>
            <input
              type="radio"
              value="walking"
              checked={travelMode === 'walking'}
              onChange={() => setTravelMode('walking')}
            />
            Walking
          </label>
          <label>
            <input
              type="radio"
              value="bicycling"
              checked={travelMode === 'bicycling'}
              onChange={() => setTravelMode('bicycling')}
            />
            Bicycling
          </label>
        </div>

        <button onClick={calculateRoute}>Get Route</button>
      </div>

      {/* Distance and Duration */}
      <div className="route-details">
        {distance && duration && (
          <div>
            <h3>Distance: {distance}</h3>
            <h3>Duration: {duration}</h3>
          </div>
        )}
      </div>

      {/* Map */}
      <div className="map-container">
        <MapContainer center={from ? from.position : [12.9716, 77.5946]} zoom={13} style={{ width: '100%', height: '400px' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {from && <Marker position={from.position}><Popup>From: {from.displayName}</Popup></Marker>}
          {to && <Marker position={to.position}><Popup>To: {to.displayName}</Popup></Marker>}
          {route && <Polyline positions={route.map(coord => [coord[1], coord[0]])} color="blue" />}
        </MapContainer>
      </div>

      {/* Popular Destinations */}
      <div className="popular-sections">
        <h2>Popular Destinations</h2>
        <div className="popular-destinations">
          {popularDestinations.map((destination, index) => (
            <div key={index} className="destination">
              <img src={destination.image} alt={destination.name} className="destination-image" />
              <div className="destination-info">
                <h3>{destination.name}</h3>
                <p>{destination.location}</p>
                <p className="rating">{destination.rating}</p>
              </div>
            </div>
          ))}
        </div>
        <Link to="/destinations">
          <button className="scroll-arrow-btn">See All Destinations</button>
        </Link>
      </div>

      {/* Popular Events */}
      <div className="popular-sections">
        <h2>Popular Events</h2>
        <div className="popular-events">
          {popularEvents.map((event, index) => (
            <div key={index} className="event">
              <img src={event.image} alt={event.name} className="event-image" />
              <div className="event-info">
                <h3>{event.name}</h3>
                <p>{event.location}</p>
                <p>{event.date}</p>
              </div>
            </div>
          ))}
        </div>
        <Link to="/events">
          <button className="scroll-arrow-btn">See All Events</button>
        </Link>
      </div>
    </div>
  );
};

export default Home;
