import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedPackage, peopleCount, pickupTime, totalCost } = location.state || {};
  const [orderId, setOrderId] = useState('');

  if (!selectedPackage) {
    navigate('/');
    return null;
  }

  // Call the backend to create a Razorpay order and get the orderId
  const createRazorpayOrder = async () => {
    try {
      const response = await fetch('http://localhost:5000/create-order', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          amount: totalCost, // Amount in INR
        }),
      });

      const data = await response.json();
      const { orderId } = data; // The orderId returned by the backend
      setOrderId(orderId);
    } catch (error) {
      console.error('Error creating Razorpay order:', error);
    }
  };

  useEffect(() => {
    createRazorpayOrder();
  }, [totalCost, selectedPackage]);

  const handlePayNow = () => {
    if (orderId) {
      // Construct the Razorpay payment URL
      const razorpayPaymentUrl = `https://checkout.razorpay.com/v1/checkout.js?order_id=${orderId}&amount=${totalCost * 100}&currency=INR`;
      window.location.href = razorpayPaymentUrl; // Redirect to Razorpay
    }
  };

  return (
    <div className="payment-page">
      <div className="payment-container">
        <h1>Payment Page</h1>
        <div className="payment-details">
          <h3>Package Details:</h3>
          <p><strong>Package:</strong> {selectedPackage.name}</p>
          <p><strong>Pickup Time:</strong> {pickupTime}</p>
          <p><strong>Number of People:</strong> {peopleCount}</p>
          <p><strong>Total Cost:</strong> â‚¹{totalCost}</p>
        </div>

        <div className="payment-actions">
          <button onClick={handlePayNow}>Pay Now</button>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
