import React, { useRef } from 'react';
import { Link } from 'react-router-dom';
import './Home.css';

// Import your images here
import iskconTempleImg from '../assets/iskcon-temple.jpg';
import lalbaghImg from '../assets/lalbagh.jpg';
import cubbonParkImg from '../assets/cubbon-park.jpg';
import concertImg from '../assets/concert.jpg';
import marathonImg from '../assets/marathon.jpg';
import foodFestivalImg from '../assets/food-festival.jpg';

const Home = () => {
  const destinationRef = useRef(null);
  const eventRef = useRef(null);

  const scroll = (ref, direction) => {
    const scrollAmount = 300; // Amount of pixels to scroll on button click
    if (ref.current) {
      ref.current.scrollBy({
        left: direction === 'right' ? scrollAmount : -scrollAmount,
        behavior: 'smooth',
      });
    }
  };

  return (
    <div className="home-container">
      {/* Location Selection Section */}
      {/* (Location Selection code remains unchanged) */}

      {/* Popular Destinations Section */}
      <div className="popular-sections">
        <h2>Popular Destinations</h2>
        <div className="popular-destinations" ref={destinationRef}>
          <div className="destination">
            <img src={iskconTempleImg} alt="ISKCON Temple" className="destination-image" />
            <div className="destination-info">
              <h3>ISKCON Temple</h3>
              <p>Rajajinagar</p>
              <div className="rating">4.2 ⭐</div>
            </div>
          </div>
          <div className="destination">
            <img src={lalbaghImg} alt="Lalbagh Botanical Garden" className="destination-image" />
            <div className="destination-info">
              <h3>Lalbagh Botanical Garden</h3>
              <p>Lalbagh</p>
              <div className="rating">4.7 ⭐</div>
            </div>
          </div>
          <div className="destination">
            <img src={cubbonParkImg} alt="Cubbon Park" className="destination-image" />
            <div className="destination-info">
              <h3>Cubbon Park</h3>
              <p>Bangalore</p>
              <div className="rating">4.6 ⭐</div>
            </div>
          </div>
          {/* Add more destinations as needed */}
        </div>
        <div className="scroll-buttons">
          <button onClick={() => scroll(destinationRef, 'left')} className="scroll-btn">⬅</button>
          <button onClick={() => scroll(destinationRef, 'right')} className="scroll-btn">➡</button>
        </div>
        <Link to="/destinations" className="view-more">Go to Popular Destinations</Link>
      </div>

      {/* Popular Events Section */}
      <div className="popular-sections">
        <h2>Popular Events</h2>
        <div className="popular-events" ref={eventRef}>
          <div className="event">
            <img src={concertImg} alt="Live Concert" className="event-image" />
            <div className="event-info">
              <h3>Live Concert by XYZ Band</h3>
              <p>Location: ABC Arena, City</p>
              <p>Date: 12th December 2024</p>
            </div>
          </div>
          <div className="event">
            <img src={marathonImg} alt="City Marathon" className="event-image" />
            <div className="event-info">
              <h3>City Marathon 2024</h3>
              <p>Location: Downtown Park</p>
              <p>Date: 5th January 2024</p>
            </div>
          </div>
          <div className="event">
            <img src={foodFestivalImg} alt="Food Festival" className="event-image" />
            <div className="event-info">
              <h3>Annual Food Festival</h3>
              <p>Location: City Square</p>
              <p>Date: 20th November 2024</p>
            </div>
          </div>
          {/* Add more events as needed */}
        </div>
        <div className="scroll-buttons">
          <button onClick={() => scroll(eventRef, 'left')} className="scroll-btn">⬅</button>
          <button onClick={() => scroll(eventRef, 'right')} className="scroll-btn">➡</button>
        </div>
        <Link to="/events" className="view-more">Go to Popular Events</Link>
      </div>
    </div>
  );
};

export default Home;
