// TripDetails.js
/* import React from 'react';
import { useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';

const TripDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { name, pickupPoint, placesOfVisit, modeOfTransport, totalPeople, travelGuide } = location.state;

  return (
    <div className="trip-details-page">
      <h1>Trip Details</h1>
      <p><strong>Name:</strong> {name}</p>
      <p><strong>Pickup Point:</strong> {pickupPoint}</p>
      <p><strong>Places of Visit:</strong> {placesOfVisit}</p>
      <p><strong>Mode of Transport:</strong> {modeOfTransport}</p>
      <p><strong>Total No. of People:</strong> {totalPeople}</p>
      <p><strong>Travel Guide:</strong> {travelGuide}</p>
      
      <button onClick={() => navigate('/book-a-trip')}>EDIT</button>
      <button onClick={() => navigate('/payment')}>CONFIRM</button>
    </div>
  );
};

export default TripDetails;
 */

import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const calculateAmount = (tripData) => {
  let baseAmount = 0;

  switch (tripData.transport) {
    case 'Bus':
      baseAmount = 100;
      break;
    case 'Car':
      baseAmount = 200;
      break;
    case 'Flight':
      baseAmount = 500;
      break;
    default:
      baseAmount = 150;
  }

  const perPersonCost = 50;
  const totalCost = baseAmount + (tripData.totalPeople * perPersonCost);

  const placeCost = tripData.placesOfVisit.split(',').length * 30;

  const totalAmount = totalCost + placeCost;

  return totalAmount;
};

const TripDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  const [showAmount, setShowAmount] = useState(false);

  const tripData = location.state || {
    name: '',
    pickupPoint: '',
    placesOfVisit: '',
    transport: '',
    totalPeople: '',
    amount: 0,
  };

  const handleEdit = () => {
    navigate('/bookatrip', { state: tripData });
  };

  const handlePayment = () => {
    const amount = calculateAmount(tripData); // Calculate amount
    setShowAmount(true);
    
    setTimeout(() => {
      navigate('/payment', { state: { amount } }); // Pass calculated amount to payment page
    }, 2000);
  };

  return (
    <div className="trip-details">
      <h1>Trip Details</h1>
      <p>Name: {tripData.name}</p>
      <p>Pickup Point: {tripData.pickupPoint}</p>
      <p>Places of Visit: {tripData.placesOfVisit}</p>
      <p>Mode of Transport: {tripData.transport}</p>
      <p>Total No. of People: {tripData.totalPeople}</p>
      
      {showAmount && <p>Amount to be Paid: ${calculateAmount(tripData)}</p>}
      
      <button onClick={handleEdit}>Edit</button>
      <button onClick={handlePayment}>Proceed to Payment</button>
    </div>
  );
};

export default TripDetails;
