// PackageDetails.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './PackageDetails.css';

const PackageDetails = () => {
  const { packageId } = useParams();

  // Sample details for each package. These could be fetched from an API.
  const packageDetails = {
    1: { name: 'Beach Paradise', description: 'Enjoy an all-inclusive beach experience with meals and activities.', price: '$2000' },
    2: { name: 'Mountain Adventure', description: 'Explore the scenic mountains with guided hikes and activities.', price: '$1500' },
    3: { name: 'City Lights', description: 'Discover the best of city life, day and night tours included.', price: '$1800' },
    4: { name: 'Wild Safari', description: 'An unforgettable safari experience with wildlife spotting.', price: '$2500' },
  };

  const pkg = packageDetails[packageId] || {};

  return (
    <div className="package-details-container">
      <h1>{pkg.name}</h1>
      <p>{pkg.description}</p>
      <p><strong>Price:</strong> {pkg.price}</p>
    </div>
  );
};

export default PackageDetails;
