import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Profile.css';

const Profile = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [gender, setGender] = useState('');
  const [dob, setDob] = useState('');
  const [image, setImage] = useState(null);
  const navigate = useNavigate();

  // Fetch email from localStorage and populate the profile form
  useEffect(() => {
    const savedEmail = localStorage.getItem('email');
    setEmail(savedEmail || ''); // Automatically populate email from localStorage

    // Fetch other profile data if needed
    const savedName = localStorage.getItem('name');
    const savedGender = localStorage.getItem('gender');
    const savedDob = localStorage.getItem('dob');
    const savedImage = localStorage.getItem('image');
    
    setName(savedName || '');
    setGender(savedGender || '');
    setDob(savedDob || '');
    setImage(savedImage || null);
  }, []);

  // Handle form input changes
  const handleNameChange = (e) => setName(e.target.value);
  const handleEmailChange = (e) => setEmail(e.target.value);
  const handleGenderChange = (e) => setGender(e.target.value);
  const handleDobChange = (e) => setDob(e.target.value);
  const handleImageChange = (e) => setImage(e.target.files[0]);

  // Handle form submission
  const handleSave = async () => {
    const formData = new FormData();
    formData.append('name', name);
    formData.append('email', email);
    formData.append('gender', gender);
    formData.append('dob', dob);
    if (image) formData.append('image', image);

    try {
      // Save to localStorage directly (or send to server)
      localStorage.setItem('name', name);
      localStorage.setItem('email', email);  // This should be handled from signup, but we can update here if needed
      localStorage.setItem('gender', gender);
      localStorage.setItem('dob', dob);
      if (image) {
        const formImageData = new FormData();
        formImageData.append('image', image);
        const response = await axios.post('http://localhost:5000/api/profile', formImageData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });
        localStorage.setItem('image', response.data);  // Store image in localStorage (server response)
      }

      alert('Profile saved successfully!');
    } catch (error) {
      console.error('Error saving profile:', error);
    }
  };

  // Logout functionality
  const handleLogout = () => {
    // Clear all user data from localStorage
    localStorage.removeItem('email');
    localStorage.removeItem('name');
    localStorage.removeItem('gender');
    localStorage.removeItem('dob');
    localStorage.removeItem('image');
    
    // Redirect to login page
    navigate('/login');
  };

  const goToTravelHistory = () => {
    navigate('/travel-history');
  };

  return (
    <div className="profile-page">
      <h2>MY PROFILE</h2>
      <div className="profile-container">
        <div className="profile-info">
          <label>Name:</label>
          <input type="text" value={name} onChange={handleNameChange} placeholder="Enter your name" />

          <label>Email:</label>
          <input type="email" value={email} onChange={handleEmailChange} placeholder="Enter your email" disabled />

          <label>Gender:</label>
          <select value={gender} onChange={handleGenderChange}>
            <option value="">Select Gender</option>
            <option value="male">Male</option>
            <option value="female">Female</option>
            <option value="other">Other</option>
          </select>

          <label>Date of Birth:</label>
          <input type="date" value={dob} onChange={handleDobChange} />

          <button className="travel-history-btn" onClick={goToTravelHistory}>Travel History</button>
          <button className="save-btn" onClick={handleSave}>Save</button>
        </div>

        <div className="profile-photo-section">
          <div className="profile-photo-placeholder">
            {image && (
              <img src={`http://localhost:5000/uploads/${image}`} alt="Profile" className="profile-photo" />
            )}
          </div>
          <input type="file" onChange={handleImageChange} />
          <button className="edit-photo-btn">Edit</button>
        </div>
      </div>

      {/* Logout Button */}
      <button className="logout-btn" onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Profile;
