// src/App.js

import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './components/Home';
import ContactUs from './components/ContactUs';
import PopularDestinations from './components/PopularDestinations';
import DestinationDetail from './components/DestinationDetail';
import PopularEvents from './components/PopularEvents';
import Profile from './components/Profile';
import TravelHistory from './components/TravelHistory';
import About from './components/About';
import TravelPackage from './components/TravelPackage';
import BookATrip from './components/BookATrip';
import PackageDetails from './components/PackageDetails';
import TripDetails from './components/TripDetails';
import TravelGuideDetails from './components/TravelGuideDetails';
import Login from './components/Login';
import Signup from './components/Signup';
import Layout from './components/Layout';
import PaymentPage from './components/PaymentPage';
import BookingConfirmation from './components/BookingConfirmation';
import PaymentConfirmation from './components/PaymentConfirmation';
import TripFare from './components/TripFare';
import TripPayment from './components/TripPayment';
import TripPaymentConfirmation from './components/TripPaymentConfirmation';

function App() {
  const [tripData, setTripData] = useState(null);

  // Check if the user is authenticated on app load
  const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true';

  return (
    <Router>
      <Routes>
        {/* Public routes: Login and Signup */}
        <Route path="/login" element={isAuthenticated ? <Navigate to="/home" /> : <Login />} />
        <Route path="/signup" element={isAuthenticated ? <Navigate to="/home" /> : <Signup />} />

        {/* Protected routes (wrapped with Layout component) */}
        <Route element={<Layout />}>
          <Route path="/home" element={isAuthenticated ? <Home /> : <Navigate to="/login" />} />
          <Route path="/about" element={isAuthenticated ? <About /> : <Navigate to="/login" />} />
          <Route path="/contact" element={isAuthenticated ? <ContactUs /> : <Navigate to="/login" />} />
          <Route path="/destinations" element={isAuthenticated ? <PopularDestinations /> : <Navigate to="/login" />} />
          <Route path="/" element={isAuthenticated ? <PopularDestinations /> : <Navigate to="/login" />} />
          <Route path="/destinations/:destinationId" element={isAuthenticated ? <DestinationDetail /> : <Navigate to="/login" />} />
          <Route path="/events" element={isAuthenticated ? <PopularEvents /> : <Navigate to="/login" />} />
          <Route path="/travelpackage" element={isAuthenticated ? <TravelPackage /> : <Navigate to="/login" />} />
          <Route path="/bookatrip" element={isAuthenticated ? <BookATrip setTripData={setTripData} /> : <Navigate to="/login" />} />
          <Route path="/packagedetails/:packageId" element={isAuthenticated ? <PackageDetails /> : <Navigate to="/login" />} />
          <Route path="/trip-details/:id" element={isAuthenticated ? <TripDetails /> : <Navigate to="/login" />} />
          <Route path="/trip-fare" element={isAuthenticated ? <TripFare /> : <Navigate to="/login" />} />
          <Route path="/travel-guide-details/:guideName" element={isAuthenticated ? <TravelGuideDetails /> : <Navigate to="/login" />} />
          <Route path="/profile" element={isAuthenticated ? <Profile /> : <Navigate to="/login" />} />
          <Route path="/travel-history" element={isAuthenticated ? <TravelHistory /> : <Navigate to="/login" />} />
          <Route path="/paymentpage" element={isAuthenticated ? <PaymentPage /> : <Navigate to="/login" />} />
          <Route path="/bookingconfirmation" element={isAuthenticated ? <BookingConfirmation /> : <Navigate to="/login" />} />
          <Route path="/paymentconfirmation" element={isAuthenticated ? <PaymentConfirmation /> : <Navigate to="/login" />} />
          <Route path="/trip-payment" element={isAuthenticated ? <TripPayment /> : <Navigate to="/login" />} />
          <Route path="/payment-confirmation" element={isAuthenticated ? <TripPaymentConfirmation /> : <Navigate to="/login" />} />
        </Route>
      </Routes>
    </Router>
  );
}

export default App;
