import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { selectedPackage, peopleCount, pickupTime, totalCost } = location.state || {};

  if (!selectedPackage) {
    // Redirect if no package data is found
    navigate('/');
    return null;
  }

  // Sample Razorpay payment URL for testing
  // Using INR as the currency and setting a test order ID
  const paymentUrl = `https://checkout.razorpay.com/v1/checkout.js?order_id=order_12345&amount=${totalCost * 100}&currency=INR`; 

  const handlePayNow = () => {
    // For demonstration, redirecting to Razorpay's sandbox payment URL
    window.location.href = paymentUrl;
  };

  return (
    <div className="payment-page">
      <div className="payment-container">
        <h1>Payment Page</h1>
        <div className="payment-details">
          <h3>Package Details:</h3>
          <p><strong>Package:</strong> {selectedPackage.name}</p>
          <p><strong>Pickup Time:</strong> {pickupTime}</p>
          <p><strong>Number of People:</strong> {peopleCount}</p>
          <p><strong>Total Cost:</strong> ₹{totalCost}</p>
        </div>

        <div className="payment-actions">
          <button onClick={handlePayNow}>Pay Now</button>
        </div>
      </div>
    </div>
  );
};

export default PaymentPage;
