import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const TripDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  const [showAmount, setShowAmount] = useState(false);

  // Retrieve tripData from location.state, with default values if missing
  const tripData = location.state || {
    name: '',
    pickupPoint: '',
    placesOfVisit: '',
    transport: '',
    totalPeople: '',
    amount: 0, // Adding amount as a default property
  };

  const handleEdit = () => {
    // Navigate back to BookATrip, passing the current trip data
    navigate('/bookatrip', { state: tripData });
  };

  const handlePayment = () => {
    setShowAmount(true); // Show amount to be paid before redirecting
    setTimeout(() => {
      navigate('/payment', { state: { amount: tripData.amount } });
    }, 2000); // Redirect to payment after 2 seconds
  };

  return (
    <div className="trip-details">
      <h1>Trip Details</h1>
      <p>Name: {tripData.name}</p>
      <p>Pickup Point: {tripData.pickupPoint}</p>
      <p>Places of Visit: {tripData.placesOfVisit}</p>
      <p>Mode of Transport: {tripData.transport}</p>
      <p>Total No. of People: {tripData.totalPeople}</p>
      
      {/* Show Payment Amount when Payment button is clicked */}
      {showAmount && <p>Amount to be Paid: ${tripData.amount}</p>}
      
      <button onClick={handleEdit}>Edit</button>
      <button onClick={handlePayment}>Proceed to Payment</button>
    </div>
  );
};

export default TripDetails;
