import React from 'react';
import { useParams } from 'react-router-dom';
import './DestinationDetail.css';

function DestinationDetail() {
  const { destinationName } = useParams();

  const destinationDetails = {
    'iskcon-temple': {
      name: 'ISKCON Temple',
      location: 'Rajajinagar',
      description: 'A famous Hindu temple dedicated to Lord Krishna.',
      image: '/images/iskcon-temple.jpg'
    },
    'lalbagh': {
      name: 'Lalbagh Botanical Garden',
      location: 'Bangalore',
      description: 'A beautiful botanical garden with rare species of plants.',
      image: '/images/lalbagh.jpg'
    },
    // Add other destinations here
  };

  const destination = destinationDetails[destinationName];

  if (!destination) {
    return <p>Destination not found</p>;
  }

  return (
    <div className="destination-detail">
      <h1>{destination.name}</h1>
      <img src={destination.image} alt={destination.name} className="destination-detail-image" />
      <p>{destination.description}</p>
      <p>Location: {destination.location}</p>
    </div>
  );
}

export default DestinationDetail;
