// TripDetails.js

import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const TripDetails = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Retrieve tripData from location.state, or provide default values if it's missing
  const tripData = location.state || {
    name: '',
    pickupPoint: '',
    placesOfVisit: '',
    transport: '',
    totalPeople: '',
  };

  const handleEdit = () => {
    // Navigate back to BookATrip, passing the current trip data
    navigate('/bookatrip', { state: tripData });
  };

  return (
    <div className="trip-details">
      <h1>Trip Details</h1>
      <p>Name: {tripData.name}</p>
      <p>Pickup Point: {tripData.pickupPoint}</p>
      <p>Places of Visit: {tripData.placesOfVisit}</p>
      <p>Mode of Transport: {tripData.transport}</p>
      <p>Total No. of People: {tripData.totalPeople}</p>
      
      {/* Edit Button */}
      <button onClick={handleEdit}>Edit</button>
    </div>
  );
};

export default TripDetails;
