import React from 'react';
import { useLocation, useParams } from 'react-router-dom';

const TripDetails = () => {
  const { state } = useLocation();  // Get the trip data from the state
  const { id } = useParams();  // Get the trip ID from the URL (if needed)

  if (!state) {
    return <div>Loading...</div>;
  }

  return (
    <div className="trip-details-page">
      <h1>Trip Details</h1>
      <div className="trip-info">
        <p><strong>Name:</strong> {state.name}</p>
        <p><strong>Pickup Point:</strong> {state.pickupPoint}</p>
        <p><strong>Places of Visit:</strong> {state.placesOfVisit}</p>
        <p><strong>Mode of Transport:</strong> {state.modeOfTransport}</p>
        <p><strong>Total People:</strong> {state.totalPeople}</p>
      </div>
    </div>
  );
};

export default TripDetails;
