/* global google */
import React, { useState, useEffect } from 'react';

// Importing necessary components from Google Maps API
import { GoogleMap, LoadScript, DirectionsRenderer } from '@react-google-maps/api';
import axios from 'axios';

const Home = () => {
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('DRIVING');

  const [loadingFromSuggestions, setLoadingFromSuggestions] = useState(false);
  const [loadingToSuggestions, setLoadingToSuggestions] = useState(false);

  const googleMapsApiKey = 'YOUR_GOOGLE_MAPS_API_KEY';

  // Debounced function to fetch suggestions
  const fetchSuggestions = async (query, isFromLocation = true) => {
    if (query.length > 2) {
      const url = `https://maps.googleapis.com/maps/api/place/autocomplete/json?input=${query}&key=${googleMapsApiKey}`;
      const loadingSetter = isFromLocation ? setLoadingFromSuggestions : setLoadingToSuggestions;
      const setter = isFromLocation ? setFromSuggestions : setToSuggestions;

      loadingSetter(true);
      try {
        const response = await axios.get(url);
        setter(response.data.predictions);
      } catch (error) {
        console.error("Error fetching suggestions:", error);
      } finally {
        loadingSetter(false);
      }
    }
  };

  useEffect(() => {
    if (from.length > 2) {
      fetchSuggestions(from, true);
    }
  }, [from]);

  useEffect(() => {
    if (to.length > 2) {
      fetchSuggestions(to, false);
    }
  }, [to]);

  const handleSuggestionClick = (suggestion, isFromLocation = true) => {
    if (isFromLocation) {
      setFrom(suggestion.description);
      setFromSuggestions([]);
    } else {
      setTo(suggestion.description);
      setToSuggestions([]);
    }
  };

  const calculateRoute = async () => {
    if (from && to) {
      setRoute(null);
      setDistance('');
      setDuration('');

      const directionsService = new google.maps.DirectionsService();
      const request = {
        origin: from,
        destination: to,
        travelMode: google.maps.TravelMode[travelMode],
      };

      directionsService.route(request, (result, status) => {
        if (status === google.maps.DirectionsStatus.OK) {
          setRoute(result);
          const routeData = result.routes[0].legs[0];
          setDistance(routeData.distance.text);
          setDuration(routeData.duration.text);
        } else {
          console.error("Error calculating route:", status);
        }
      });
    }
  };

  return (
    <div className="home-container">
      <div className="location-select">
        {/* From Location Input */}
        <div className="location-input">
          <input
            type="text"
            value={from}
            placeholder="Enter starting location"
            onChange={(e) => setFrom(e.target.value)}
            onFocus={() => fetchSuggestions(from, true)}
          />
          {loadingFromSuggestions && <div className="loader">Loading...</div>}
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion) => (
                <li
                  key={suggestion.place_id}
                  onClick={() => handleSuggestionClick(suggestion, true)}
                >
                  {suggestion.description}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* To Location Input */}
        <div className="location-input">
          <input
            type="text"
            value={to}
            placeholder="Enter destination"
            onChange={(e) => setTo(e.target.value)}
            onFocus={() => fetchSuggestions(to, false)}
          />
          {loadingToSuggestions && <div className="loader">Loading...</div>}
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion) => (
                <li
                  key={suggestion.place_id}
                  onClick={() => handleSuggestionClick(suggestion, false)}
                >
                  {suggestion.description}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Travel Mode Selector */}
        <div className="travel-mode-select">
          <label htmlFor="travel-mode">Travel Mode:</label>
          <select
            id="travel-mode"
            value={travelMode}
            onChange={(e) => setTravelMode(e.target.value)}
          >
            <option value="DRIVING">Driving</option>
            <option value="BICYCLING">Bicycling</option>
            <option value="WALKING">Walking</option>
          </select>
        </div>

        <button onClick={calculateRoute}>Calculate Route</button>
      </div>

      {/* Route Information */}
      {distance && duration && (
        <div className="route-info">
          <p><strong>Distance:</strong> {distance}</p>
          <p><strong>Duration:</strong> {duration}</p>
        </div>
      )}

      {/* Google Map */}
      <div className="map-container">
        <LoadScript googleMapsApiKey={googleMapsApiKey}>
          <GoogleMap
            mapContainerStyle={{
              width: '100%',
              height: '100%',
              borderRadius: '15px',
            }}
            center={{
              lat: 37.7749,  // Default to San Francisco lat/lng if no route
              lng: -122.4194,
            }}
            zoom={14}
          >
            {route && <DirectionsRenderer directions={route} />}
          </GoogleMap>
        </LoadScript>
      </div>
    </div>
  );
};

export default Home;
