import React, { useState, useRef } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

import iskconTempleImg from '../images/iskcon-temple.jpg';
import lalbaghImg from '../images/lalbagh.jpg';
import cubbonParkImg from '../images/cubbon-park.jpg';
import concertImg from '../images/concert.jpg';
import marathonImg from '../images/marathon.jpg';
import foodFestivalImg from '..//food-festival.jpg';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);
  
  const fromRef = useRef(null);
  const toRef = useRef(null);

  const PHOTON_API_URL = process.env.REACT_APP_PHOTON_API_URL;  // Photon API URL
  const OSRM_API_URL = process.env.REACT_APP_OSRM_API_URL;      // OSRM API URL

  // Debounce function for handling input change delay
  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  // Fetch place suggestions using Photon API
  const fetchSuggestions = async (location, setter, setSuggestions) => {
    if (location.length === 0) {
      setSuggestions([]);
      return;
    }

    const url = `${PHOTON_API_URL}?q=${encodeURIComponent(location)}&lang=en&limit=5`;
    try {
      const response = await axios.get(url);
      // Check the response from the API
      console.log("Response from Photon API:", response.data);
      setSuggestions(response.data.features.map((place) => ({
        displayName: place.properties.name,
        position: { lat: place.geometry.coordinates[1], lng: place.geometry.coordinates[0] },
      })));
    } catch (error) {
      console.error('Error fetching places:', error);
      setSuggestions([]);
    }
  };

  // Handle place change with debounce
  const handlePlaceChange = debounce((location, setter, setSuggestions) => {
    fetchSuggestions(location, setter, setSuggestions);
  }, 300);

  // Select a suggestion and update the input field
  const selectSuggestion = (suggestion, setter, inputRef, setSuggestions) => {
    setter(suggestion.position); // Set the place position
    inputRef.current.value = suggestion.displayName; // Set the display name in the input
    setSuggestions([]); // Clear the suggestions
  };

  // Calculate route using OSRM API
  const calculateRoute = async () => {
    if (from && to) {
      setRoute(null);  // Reset previous route
      setDistance('');
      setDuration('');

      // Construct the OSRM URL with correct formatting for coordinates
      const url = `${OSRM_API_URL}/driving/${from.lng},${from.lat};${to.lng},${to.lat}?overview=false&steps=true`;

      try {
        const response = await axios.get(url);
        const routeData = response.data.routes[0];
        setRoute(routeData);
        setDistance(routeData.distance);  // in meters
        setDuration(routeData.duration);  // in seconds
      } catch (error) {
        console.error("Error calculating route:", error);
      }
    } else {
      alert("Please select both the start and end locations.");
    }
  };

  // Swap the start and end locations
  const swapLocations = () => {
    const temp = from;
    setFrom(to);
    setTo(temp);
    setFromSuggestions([]); // Clear the suggestions when swapping
    setToSuggestions([]);
  };

  // Bangalore specific popular destinations
  const popularDestinations = [
    {
      name: "ISKCON Temple",
      location: "Rajajinagar, Bangalore",
      rating: "4.2 ⭐",
      image: iskconTempleImg
    },
    {
      name: "Lalbagh Botanical Garden",
      location: "Mavalli, Bangalore",
      rating: "4.7 ⭐",
      image: lalbaghImg
    },
    {
      name: "Cubbon Park",
      location: "Bangalore",
      rating: "4.5 ⭐",
      image: cubbonParkImg
    },
  ];

  // Bangalore specific popular events
  const popularEvents = [
    {
      name: "Live Concert by XYZ Band",
      location: "ABC Arena, Bangalore",
      date: "12th December 2024",
      image: concertImg
    },
    {
      name: "City Marathon 2024",
      location: "Downtown Park, Bangalore",
      date: "5th January 2024",
      image: marathonImg
    },
    {
      name: "Annual Food Festival",
      location: "City Square, Bangalore",
      date: "20th November 2024",
      image: foodFestivalImg
    },
  ];

  return (
    <div className="home-container">
      {/* Location Selection Section */}
      <div className="location-select">
        {/* From Location Input */}
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="From"
            ref={fromRef}
            onChange={(e) => handlePlaceChange(e.target.value, setFrom, setFromSuggestions)}
          />
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setFrom, fromRef, setFromSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Swap Button */}
        <button onClick={swapLocations}>⇄</button>

        {/* To Location Input */}
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="To"
            ref={toRef}
            onChange={(e) => handlePlaceChange(e.target.value, setTo, setToSuggestions)}
          />
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setTo, toRef, setToSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Get Route Button */}
        <button onClick={calculateRoute}>Get Route</button>
      </div>

      {/* Travel Mode Selection */}
      <div className="travel-mode-select">
        <label htmlFor="travelMode">Travel Mode: </label>
        <select
          id="travelMode"
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
        >
          <option value="car">Car</option>
          <option value="bike">Bike</option>
          <option value="foot">Walking</option>
        </select>
      </div>

      {/* Popular Destinations and Events Sections */}
      <div className="popular-sections">
        {/* Popular Destinations */}
        <div className="popular-destinations">
          {popularDestinations.map((destination, index) => (
            <div key={index} className="destination">
              <img src={destination.image} alt={destination.name} className="destination-image" />
              <div className="destination-info">
                <h3>{destination.name}</h3>
                <p>{destination.location}</p>
                <div className="rating">{destination.rating}</div>
              </div>
            </div>
          ))}
        </div>

        {/* Horizontal Scroll Arrow */}
        <button className="scroll-arrow-btn">⟶</button>

        {/* Popular Events */}
        <div className="popular-events">
          {popularEvents.map((event, index) => (
            <div key={index} className="event">
              <img src={event.image} alt={event.name} className="event-image" />
              <div className="event-info">
                <h3>{event.name}</h3>
                <p>Location: {event.location}</p>
                <p>Date: {event.date}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
