import React, { useState, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Polyline } from 'react-leaflet';
import axios from 'axios';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import './Home.css';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving-car'); // Default travel mode
  const [suggestionsFrom, setSuggestionsFrom] = useState([]);
  const [suggestionsTo, setSuggestionsTo] = useState([]);

  const fromRef = useRef(null);
  const toRef = useRef(null);

  const handlePlaceChange = async (location, setter, setSuggestions) => {
    if (location.length === 0) {
      setSuggestions([]); // Clear suggestions if input is empty
      return;
    }
    const url = `https://nominatim.openstreetmap.org/search?format=json&q=${location}`;
    const response = await axios.get(url);
    setSuggestions(response.data.map(place => ({
      displayName: place.display_name,
      position: { lat: parseFloat(place.lat), lng: parseFloat(place.lon) },
    })));
    if (response.data.length > 0) {
      const place = response.data[0];
      const position = { lat: parseFloat(place.lat), lng: parseFloat(place.lon) };
      setter(position);
    }
  };

  const calculateRoute = async () => {
    if (from && to) {
      const url = `https://api.openrouteservice.org/v2/directions/${travelMode}`;
      const response = await axios.post(
        url,
        {
          coordinates: [[from.lng, from.lat], [to.lng, to.lat]],
        },
        {
          headers: {
            'Authorization': 'YOUR_API_KEY', // Replace with your OpenRouteService API key
            'Content-Type': 'application/json',
          },
        }
      );
      if (response.data && response.data.routes.length > 0) {
        const routeData = response.data.routes[0];
        const geometry = routeData.geometry.coordinates.map(coord => ({ lat: coord[1], lng: coord[0] }));
        setRoute(geometry);
        setDistance((routeData.summary.distance / 1000).toFixed(2) + ' km'); // Convert meters to km
        setDuration(Math.round(routeData.summary.duration / 60) + ' mins'); // Convert seconds to mins
      }
    }
  };

  const handleSuggestionClickFrom = (suggestion) => {
    setFrom(suggestion.position);
    fromRef.current.value = suggestion.displayName; // Update input value
    setSuggestionsFrom([]); // Clear suggestions
  };

  const handleSuggestionClickTo = (suggestion) => {
    setTo(suggestion.position);
    toRef.current.value = suggestion.displayName; // Update input value
    setSuggestionsTo([]); // Clear suggestions
  };

  const swapLocations = () => {
    const temp = from;
    setFrom(to);
    setTo(temp);
  };

  return (
    <div className="home-container">
      {/* Location Input Section */}
      <div className="location-select">
        <input
          type="text"
          placeholder="From"
          ref={fromRef}
          onBlur={() => handlePlaceChange(fromRef.current.value, setFrom, setSuggestionsFrom)}
          onChange={(e) => handlePlaceChange(e.target.value, setFrom, setSuggestionsFrom)}
        />
        
        {suggestionsFrom.length > 0 && (
          <ul className="suggestions-list">
            {suggestionsFrom.map((suggestion, index) => (
              <li key={index} onClick={() => handleSuggestionClickFrom(suggestion)}>
                {suggestion.displayName}
              </li>
            ))}
          </ul>
        )}

        <button onClick={swapLocations}>â‡„</button>

        <input
          type="text"
          placeholder="To"
          ref={toRef}
          onBlur={() => handlePlaceChange(toRef.current.value, setTo, setSuggestionsTo)}
          onChange={(e) => handlePlaceChange(e.target.value, setTo, setSuggestionsTo)}
        />
        
        {suggestionsTo.length > 0 && (
          <ul className="suggestions-list">
            {suggestionsTo.map((suggestion, index) => (
              <li key={index} onClick={() => handleSuggestionClickTo(suggestion)}>
                {suggestion.displayName}
              </li>
            ))}
          </ul>
        )}

        <button onClick={calculateRoute}>Get Route</button>
      </div>

      {/* Travel Mode Selection */}
      <div className="travel-mode-select">
        <label htmlFor="travelMode">Travel Mode: </label>
        <select
          id="travelMode"
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
        >
          <option value="driving-car">Driving</option>
          <option value="cycling-regular">Cycling</option>
          <option value="foot-walking">Walking</option>
        </select>
      </div>

      {/* Map Display */}
      <MapContainer center={from || { lat: 37.7749, lng: -122.4194 }} zoom={13} style={{ height: '400px', width: '100%' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        
        {from && <Marker position={from} icon={new L.Icon({ iconUrl: 'https://leafletjs.com/examples/custom-icons/leaf-red.png', iconSize: [25, 41] })} />}
        {to && <Marker position={to} icon={new L.Icon({ iconUrl: 'https://leafletjs.com/examples/custom-icons/leaf-green.png', iconSize: [25, 41] })} />}
        
        {route && <Polyline positions={route} color="blue" />}
      </MapContainer>

      {/* Distance and Duration Info */}
      <div className="route-info">
        {distance && duration && (
          <div>
            <p>Distance: {distance}</p>
            <p>Approximate Time: {duration}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
