import React, { useState, useRef } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [from, setFrom] = useState(null);
  const [to, setTo] = useState(null);
  const [route, setRoute] = useState(null);
  const [distance, setDistance] = useState('');
  const [duration, setDuration] = useState('');
  const [travelMode, setTravelMode] = useState('driving');
  const [fromSuggestions, setFromSuggestions] = useState([]);
  const [toSuggestions, setToSuggestions] = useState([]);
  
  const fromRef = useRef(null);
  const toRef = useRef(null);
  
  const ORS_API_KEY = process.env.REACT_APP_ORS_API_KEY; // Make sure to add your OpenRouteService key
  const NOMINATIM_API_URL = 'https://nominatim.openstreetmap.org/search';

  const debounce = (func, delay) => {
    let timeout;
    return (...args) => {
      clearTimeout(timeout);
      timeout = setTimeout(() => {
        func(...args);
      }, delay);
    };
  };

  // Fetch place suggestions from Nominatim
  const fetchSuggestions = async (location, setter, setSuggestions) => {
    if (location.length === 0) {
      setSuggestions([]);
      return;
    }

    const url = `${NOMINATIM_API_URL}?q=${location}&format=json&addressdetails=1&limit=5`;
    try {
      const response = await axios.get(url);
      setSuggestions(response.data.map((place) => ({
        displayName: place.display_name,
        position: { lat: parseFloat(place.lat), lng: parseFloat(place.lon) },
      })));
    } catch (error) {
      console.error("Error fetching places:", error);
    }
  };

  // Handle place change with debounce
  const handlePlaceChange = debounce((location, setter, setSuggestions) => {
    fetchSuggestions(location, setter, setSuggestions);
  }, 300);

  // Select a suggestion and update the input field
  const selectSuggestion = (suggestion, setter, inputRef, setSuggestions) => {
    setter(suggestion.position); // Set the place position
    inputRef.current.value = suggestion.displayName; // Set the display name in the input
    setSuggestions([]); // Clear the suggestions
  };

  // Calculate route using OpenRouteService
  const calculateRoute = async () => {
    if (from && to) {
      setRoute(null);  // Reset previous route
      setDistance('');
      setDuration('');

      const request = {
        coordinates: [
          [from.lng, from.lat], // from
          [to.lng, to.lat],     // to
        ],
        profile: travelMode, // 'driving', 'cycling', 'foot'
      };

      try {
        const response = await axios.post('https://api.openrouteservice.org/v2/directions', request, {
          headers: {
            Authorization: ORS_API_KEY,
            'Content-Type': 'application/json',
          },
        });
        const routeData = response.data.routes[0];
        setRoute(routeData);
        setDistance(routeData.summary.distance); // in meters
        setDuration(routeData.summary.duration); // in seconds
      } catch (error) {
        console.error("Error calculating route:", error);
      }
    } else {
      alert("Please select both the start and end locations.");
    }
  };

  // Swap the start and end locations
  const swapLocations = () => {
    const temp = from;
    setFrom(to);
    setTo(temp);
    setFromSuggestions([]); // Clear the suggestions when swapping
    setToSuggestions([]);
  };

  return (
    <div className="home-container">
      {/* Location Selection Section */}
      <div className="location-select">
        {/* From Location Input */}
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="From"
            ref={fromRef}
            onChange={(e) => handlePlaceChange(e.target.value, setFrom, setFromSuggestions)}
          />
          {fromSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {fromSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setFrom, fromRef, setFromSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Swap Button */}
        <button onClick={swapLocations}>⇄</button>

        {/* To Location Input */}
        <div style={{ position: 'relative' }}>
          <input
            type="text"
            placeholder="To"
            ref={toRef}
            onChange={(e) => handlePlaceChange(e.target.value, setTo, setToSuggestions)}
          />
          {toSuggestions.length > 0 && (
            <ul className="suggestions-list">
              {toSuggestions.map((suggestion, index) => (
                <li key={index} onClick={() => selectSuggestion(suggestion, setTo, toRef, setToSuggestions)}>
                  {suggestion.displayName}
                </li>
              ))}
            </ul>
          )}
        </div>

        {/* Get Route Button */}
        <button onClick={calculateRoute}>Get Route</button>
      </div>

      {/* Travel Mode Selection */}
      <div className="travel-mode-select">
        <label htmlFor="travelMode">Travel Mode: </label>
        <select
          id="travelMode"
          value={travelMode}
          onChange={(e) => setTravelMode(e.target.value)}
        >
          <option value="driving">Driving</option>
          <option value="cycling">Cycling</option>
          <option value="foot">Walking</option>
        </select>
      </div>

      {/* OpenStreetMap with Routing */}
      <div className="map-container">
        {/* You can display OpenStreetMap with a library like Leaflet */}
        <iframe
          src={`https://www.openstreetmap.org/?mlat=${from ? from.lat : 37.7749}&mlon=${from ? from.lng : -122.4194}&zoom=13`}
          width="100%"
          height="400px"
          title="OpenStreetMap"
        ></iframe>
      </div>

      {/* Route Information */}
      <div className="route-info">
        {distance && duration ? (
          <div>
            <p>Distance: {distance} meters</p>
            <p>Approximate Time: {duration} seconds</p>
          </div>
        ) : (
          <p>Please select start and end locations to calculate the route.</p>
        )}
      </div>

      {/* About Section */}
      <div className="app-details">
        <h2>About Our Travel Buddy App</h2>
        <p>Welcome to our Travel Buddy app! We help you plan your journeys with ease.</p>
        <h3>Our Services</h3>
        <ul>
          <li>Route planning and navigation</li>
          <li>Travel distance and time estimation</li>
          <li>Place suggestions for your travel needs</li>
          <li>Multiple travel modes for your convenience</li>
        </ul>
      </div>

      {/* Popular Destinations Section */}
      <div className="popular-destinations">
        <h2>Popular Destinations</h2>
        <div className="destination">
          <img src="/path/to/iskcon-temple.jpg" alt="ISKCON Temple" className="destination-image" />
          <div className="destination-info">
            <h3>ISKCON Temple</h3>
            <p>Rajajinagar</p>
            <div className="rating">4.2 ⭐</div>
          </div>
        </div>
        <div className="destination">
          <img src="/path/to/mysore-palace.jpg" alt="Mysore Palace" className="destination-image" />
          <div className="destination-info">
            <h3>Mysore Palace</h3>
            <p>Mysore</p>
            <div className="rating">4.8 ⭐</div>
          </div>
        </div>
        <div className="destination">
          <img src="/path/to/anjaneya-hills.jpg" alt="Anjaneya Hills" className="destination-image" />
          <div className="destination-info">
            <h3>Anjaneya Hills</h3>
            <p>Hampi</p>
            <div className="rating">4.5 ⭐</div>
          </div>
        </div>
        <Link to="/destinations" className="view-more">Go to Popular Destinations</Link>
      </div>

      {/* Popular Events Section */}
      <div className="popular-events">
        <h2>Popular Events</h2>
        <div className="event">
          <img src="/path/to/alan-walker.jpg" alt="Alan Walker" className="event-image" />
          <div className="event-info">
            <h3>Alan Walker Concert</h3>
            <p>Forum Mall</p>
            <div className="rating">4.2 ⭐</div>
          </div>
        </div>
        <div className="event">
          <img src="/path/to/food-festival.jpg" alt="Food Festival" className="event-image" />
          <div className="event-info">
            <h3>Food Festival</h3>
            <p>Lalbagh</p>
            <div className="rating">4.7 ⭐</div>
          </div>
        </div>
        <div className="event">
          <img src="/path/to/art-expo.jpg" alt="Art Expo" className="event-image" />
          <div className="event-info">
            <h3>Art Expo</h3>
            <p>City Art Gallery</p>
            <div className="rating">4.6 ⭐</div>
          </div>
        </div>
        <Link to="/events" className="view-more">Go to Popular Events</Link>
      </div>
    </div>
  );
};

export default Home;
