import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './BookATrip.css';

// Import Model Data
import  carModels  from './CarModels';
import { bikeModels } from './BikeModels';
import { busModels } from './BusModels';

const BookATrip = () => {
  const [name, setName] = useState('');
  const [pickupPoint, setPickupPoint] = useState('');
  const [dropPoint, setDropPoint] = useState('');
  const [modeOfTransport, setModeOfTransport] = useState('');
  const [totalPeople, setTotalPeople] = useState('');
  const [selectedModel, setSelectedModel] = useState(null);  // State to store selected model
  const navigate = useNavigate();

  const handleBookNow = async () => {
    const details = {
      name,
      pickupPoint,
      dropPoint,
      modeOfTransport,
      totalPeople,
      selectedModel, // Add the selected model to the details
    };

    try {
      const response = await fetch('http://localhost:5000/api/trips', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(details),
      });

      if (response.ok) {
        const trip = await response.json();
        navigate(`/trip-details/${trip.id}`, { state: trip });
      } else {
        alert('Failed to book trip');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error booking the trip');
    }
  };

  const handleSelectModel = (model) => {
    setSelectedModel(model);  // Store the selected model in state
    setModeOfTransport(model.name); // Optionally set the mode of transport to match model name
  };

  // Predefined list of places in Bengaluru
  const places = [
    'Koramangala', 'Indiranagar', 'MG Road', 'Whitefield', 'Jayanagar', 
    'BTM Layout', 'Electronic City', 'Hebbal', 'Rajajinagar', 'Banashankari'
  ];

  // Predefined list of transport modes
  const transportModes = ['Car', 'Bus', 'Bike', 'Auto', 'Cab'];

  // Predefined list of total people options
  const peopleOptions = Array.from({ length: 10 }, (_, i) => i + 1); // Creates an array [1, 2, ..., 10]

  // Render models based on transport mode
  const renderModels = () => {
    let models = [];
    if (modeOfTransport === 'Car') {
      models = carModels;
    } else if (modeOfTransport === 'Bike') {
      models = bikeModels;
    } else if (modeOfTransport === 'Bus') {
      models = busModels;
    }

    return models.length > 0 ? (
      <div className="model-list">
        {models.map((model, index) => (
          <div key={index} className="model-item" onClick={() => handleSelectModel(model)}>
            <img src={model.image} alt={model.name} className="model-image" />
            <h4>{model.name}</h4>
            <p>{model.description}</p>
          </div>
        ))}
      </div>
    ) : (
      <p>Please select a mode of transport first.</p>
    );
  };

  return (
    <div className="book-trip-page">
      <h1>BOOK A TRIP</h1>
      <div className="form-section">
        <label>Name:</label>
        <input 
          type="text" 
          value={name} 
          onChange={(e) => setName(e.target.value)} 
        />

        <label>Pickup Point:</label>
        <select 
          value={pickupPoint} 
          onChange={(e) => setPickupPoint(e.target.value)}
        >
          <option value="">Select Pickup Point</option>
          {places.map((place, index) => (
            <option key={index} value={place}>{place}</option>
          ))}
        </select>

        <label>Drop Point:</label>
        <select 
          value={dropPoint} 
          onChange={(e) => setDropPoint(e.target.value)}
        >
          <option value="">Select Drop Point</option>
          {places.map((place, index) => (
            <option key={index} value={place}>{place}</option>
          ))}
        </select>

        <label>Mode of Transport:</label>
        <select 
          value={modeOfTransport} 
          onChange={(e) => setModeOfTransport(e.target.value)}
        >
          <option value="">Select Mode of Transport</option>
          {transportModes.map((mode, index) => (
            <option key={index} value={mode}>{mode}</option>
          ))}
        </select>

        {/* Show models after selecting transport mode */}
        {modeOfTransport && renderModels()}

        <label>Total No. of People:</label>
        <select 
          value={totalPeople} 
          onChange={(e) => setTotalPeople(e.target.value)}
        >
          <option value="">Select Number of People</option>
          {peopleOptions.map((people, index) => (
            <option key={index} value={people}>{people}</option>
          ))}
        </select>

        <button className="book-now" onClick={handleBookNow}>BOOK NOW</button>
      </div>
    </div>
  );
};

export default BookATrip;
