// BookATrip.js
import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './BookATrip.css';

const BookATrip = () => {
  const [name, setName] = useState('');
  const [pickupPoint, setPickupPoint] = useState('');
  const [placesOfVisit, setPlacesOfVisit] = useState('');
  const [modeOfTransport, setModeOfTransport] = useState('');
  const [totalPeople, setTotalPeople] = useState('');
  const [travelGuide, setTravelGuide] = useState('');
  const [travelGuides, setTravelGuides] = useState(['Guide A', 'Guide B', 'Guide C']);
  
  const history = useHistory();

  const handleBookNow = () => {
    const details = {
      name,
      pickupPoint,
      placesOfVisit,
      modeOfTransport,
      totalPeople,
      travelGuide,
    };
    history.push({
      pathname: '/trip-details',
      state: details,
    });
  };

  return (
    <div className="book-trip-page">
      <h1>BOOK A TRIP</h1>
      <div className="form-section">
        <label>Name:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        <label>Pickup Point:</label>
        <input type="text" value={pickupPoint} onChange={(e) => setPickupPoint(e.target.value)} />
        <label>Places of Visit:</label>
        <input type="text" value={placesOfVisit} onChange={(e) => setPlacesOfVisit(e.target.value)} />
        <label>Mode of Transport:</label>
        <input type="text" value={modeOfTransport} onChange={(e) => setModeOfTransport(e.target.value)} />
        <label>Total No. of People:</label>
        <input type="text" value={totalPeople} onChange={(e) => setTotalPeople(e.target.value)} />

        <label>Travel Guide:</label>
        <input type="text" value={travelGuide} onChange={(e) => setTravelGuide(e.target.value)} />
        
        <div className="travel-guide-list">
          <h3>Available Travel Guides</h3>
          <ul>
            {travelGuides.map((guide, index) => (
              <li key={index}>
                {guide}
                <button onClick={() => history.push(`/travel-guide-details/${guide}`)}>Travel Guide Details</button>
              </li>
            ))}
          </ul>
        </div>

        <button className="book-now" onClick={handleBookNow}>BOOK NOW</button>
      </div>
    </div>
  );
};

export default BookATrip;
