import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './TravelPackage.css';

const TravelPackage = () => {
  const navigate = useNavigate();

  const packages = [
    { id: 1, name: 'Beach Paradise', description: 'A relaxing beach vacation with all-inclusive options.' },
    { id: 2, name: 'Mountain Adventure', description: 'Explore the mountains with guided hikes and scenic views.' },
    { id: 3, name: 'City Lights', description: 'Discover the vibrant life of the city with day and night tours.' },
    { id: 4, name: 'Wild Safari', description: 'Experience the thrill of wildlife safaris and jungle adventures.' },
  ];

  const handleBookNow = () => {
    navigate('/bookatrip');
  };

  const handlePackageDetails = (packageId) => {
    navigate(`/packagedetails/${packageId}`);
  };

  return (
    <div className="travel-package-container">
      <h1>Travel Packages</h1>

      {/* Horizontal Scrollable Section for Packages */}
      <div className="package-scroll">
        {packages.map((pkg) => (
          <div className="package-card" key={pkg.id}>
            <h3>{pkg.name}</h3>
            <p>{pkg.description}</p>
            <button onClick={() => handlePackageDetails(pkg.id)}>Package Details</button>
          </div>
        ))}
      </div>

      {/* Dropdown for Preferred Language */}
      <div className="dropdown-container">
        <label htmlFor="language-select">Preferred Language:</label>
        <select id="language-select">
          <option value="english">English</option>
          <option value="spanish">Spanish</option>
          <option value="french">French</option>
          <option value="german">German</option>
        </select>
      </div>

      {/* Dropdown for Time of Pickup */}
      <div className="dropdown-container">
        <label htmlFor="time-select">Time of Pickup:</label>
        <select id="time-select">
          <option value="8am">8:00 AM</option>
          <option value="10am">10:00 AM</option>
          <option value="12pm">12:00 PM</option>
          <option value="2pm">2:00 PM</option>
        </select>
      </div>

      {/* Book Now Button at the Bottom */}
      <div className="book-now-container">
        <button onClick={handleBookNow}>Book Now</button>
      </div>
    </div>
  );
};

export default TravelPackage;
