import React from 'react';
import { useNavigate } from 'react-router-dom';
import './TravelPackage.css';

// Import images for the travel packages
import BeachParadiseImage from '../images/beach-paradise.jpg';
import MountainAdventureImage from '../images/mountain-adventure.jpg';
import CityLightsImage from '../images/city-lights.jpg';
import WildSafariImage from '../images/wild-safari.jpg';


const TravelPackage = () => {
  const navigate = useNavigate();

  const packages = [
    { 
      id: 1, 
      name: 'Bangalore City Tour', 
      description: 'Explore the best of Bangalore with a guided city tour covering major landmarks.', 
      image: 'bangalore-city-tour.jpg', // Image related to the package
    },
    { 
      id: 2, 
      name: 'Nandi Hills Adventure', 
      description: 'Trek up to Nandi Hills for a panoramic view and enjoy a picnic at the top.',
      image: 'nandi-hills.jpg',
    },
    { 
      id: 3, 
      name: 'Ramanagara Rock Climbing', 
      description: 'Adventure awaits you in Ramanagara with rock climbing and hiking activities.',
      image: 'ramanagara-climbing.jpg',
    },
    { 
      id: 4, 
      name: 'Kabini Wildlife Safari', 
      description: 'Embark on an exciting safari through Kabini forest and spot wildlife in their natural habitat.',
      image: 'kabini-safari.jpg',
    },
  ];

  const handleBookNow = () => {
    navigate('/bookatrip');
  };

  const handlePackageDetails = (packageId) => {
    navigate(`/packagedetails/${packageId}`);
  };

  return (
    <div className="travel-package-container">
      <h1>Travel Packages - Bangalore</h1>

      {/* Horizontal Scrollable Section for Packages */}
      <div className="package-scroll">
        {packages.map((pkg) => (
          <div className="package-card" key={pkg.id}>
            <img src={pkg.image} alt={pkg.name} className="package-image" />
            <h3>{pkg.name}</h3>
            <p>{pkg.description}</p>
            <button onClick={() => handlePackageDetails(pkg.id)}>Package Details</button>
          </div>
        ))}
      </div>

      {/* Book Now Button at the Bottom */}
      <div className="book-now-container">
        <button onClick={handleBookNow}>Book Now</button>
      </div>
    </div>
  );
};

export default TravelPackage;
