import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate, useLocation } from 'react-router-dom';


import Home from './components/Home';
import ContactUs from './components/ContactUs';
import PopularDestinations from './components/PopularDestinations';
import PopularEvents from './components/PopularEvents';
import Profile from './components/Profile';
import TravelHistory from './components/TravelHistory';
import About from './components/About';
import TravelPackage from './components/TravelPackage';
import BookATrip from './components/BookATrip';
import PackageDetails from './components/PackageDetails';
import TripDetails from './components/TripDetails';
import TravelGuideDetails from './components/TravelGuideDetails';
import Payment from './components/Payment';
import './App.css';
import logoImage from './images/logo-modified.png'; // Ensure this path is correct
import Footer from './components/Footer';
import Login from './components/Login';
import Signup from './components/Signup';

function App() {
  const [tripData, setTripData] = useState(null);
  const location = useLocation(); // Get the current route using useLocation()

  // Check if we are on the Login or Signup page to hide the navigation
  const showNavbar = location.pathname !== '/login' && location.pathname !== '/signup';

  return (
    <Router>
      <div className="App">
        {/* Conditionally render the navigation bar */}
        {showNavbar && (
          <header>
            <div className="logo-container">
              <Link to="/" >
                <img src={logoImage} alt="Logo" className="header-logo" />
              </Link>
              <div className="logo-text">
                <span>TRAVEL</span>
                <span>BUDDY</span>
              </div>
            </div>
            <nav>
              <Link to="/" >Home</Link>
              <Link to="/destinations" >Popular Destinations</Link>
              <Link to="/travelpackage" >Travel Package</Link>
              <Link to="/events" >Popular Events</Link>
              <Link to="/bookatrip" >Book a Trip</Link>
              <Link to="/contact" >Contact Us</Link>
            </nav>
            <Link to="/profile" className="profile-icon">ðŸ‘¤</Link>
          </header>
        )}

        {/* Routes to different pages */}
        <Routes>
          {/* Redirecting root (/) path to /login */}
          <Route path="/" element={<Navigate to="/login" />} /> {/* Redirect to login */}

          {/* Other Routes */}
          <Route path="/home" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<ContactUs />} />
          <Route path="/destinations" element={<PopularDestinations />} />
          <Route path="/events" element={<PopularEvents />} />
          <Route path="/travelpackage" element={<TravelPackage />} />
          <Route path="/bookatrip" element={<BookATrip setTripData={setTripData} />} />
          <Route path="/packagedetails/:packageId" element={<PackageDetails />} />
          <Route path="/trip-details" element={<TripDetails tripData={tripData} />} />
          <Route path="/travel-guide-details/:guideName" element={<TravelGuideDetails />} />
          <Route path="/payment" element={<Payment />} />
          <Route path="/profile" element={<Profile />} />
          <Route path="/travel-history" element={<TravelHistory />} />

          {/* Login and Signup pages */}
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<Signup />} />
        </Routes>

        <Footer />
      </div>
    </Router>
  );
}

export default App;
